    private void dibujador ()
    {
        this.pintor=this.jPanel1.getGraphics();
        
        this.pintor.drawString("Bienvenido, qu√© pintaremos hoy", 20, 20);
        
        this.pintor.setColor(Color.green);
        this.pintor.drawLine(30, 80, 230, 30);
        
        this.pintor.setColor(Color.red);
        this.pintor.drawLine(30, 80, 230, 30);
        
        this.pintor.drawString("Felipe Abella", 10, 10);
        
        this.pintor.setColor(Color.orange);
        this.pintor.drawRect(30, 90, 200, 40);
        
        this.pintor.setColor(Color.magenta);
        this.pintor.drawOval(30, 140, 80, 80);
        this.pintor.setColor(Color.blue);
        this.pintor.fillOval(30, 140, 80, 80);
        
        this.pintor.setColor(Color.black);
        this.pintor.drawOval(40, 180, 1, 1);
    }
